mapper.update:
update tbl_emp
set emp_name = #{empName,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  d_id = #{dId,jdbcType=INTEGER}
where emp_id = #{empId,jdbcType=INTEGER}

mapper.select:
select emp_id, emp_name, gender, email, d_id
from tbl_emp
where emp_id = #{empId,jdbcType=INTEGER}
